import React from "react";

class AddUser extends React.Component  
{
  constructor(props)
  {
        super(props)
        this.state={
          firstname: "",
          lastname: "",
          bio: "",
          age: 1,
          isHappy:false,
        }
  }

  render()
  {
    return(
      <form ref={(el)=> this.myForm = el} >
        <input placeholder="Имя" onChange={(el)=>this.setState({firstname: el.target.value})}/>
        <input placeholder="Фамилия"onChange={(el)=>this.setState({lasttname: el.target.value})}/>
        <textarea placeholder="Биография"onChange={(el)=>this.setState({bio: el.target.value})}/>
        <input placeholder="Возраст"onChange={(el)=>this.setState({age: el.target.value})}/>
        <label htmlFor="isHappy">Счастлив?</label>
        <input type="checkbox" id = "isHappy"onChange={(el)=>this.setState({isHappy: el.target.checked})}/>
        <button type="button" onClick={()=> {
          this.myForm.reset()
            this.props.onAdd({
            firstname: this.state.firstname,
            lasttname: this.state.lasttname,
            bio: this.state.bio,
            age: this.state.age,
            isHappy: this.state.isHappy,
        })}}>Добавить</button>
      </form>
    )
  }

}

export default AddUser
